mapper.select:
select

from t_vote_user
where vote_id=#{voteId,jdbcType=BIGINT}
and user_id=#{userId,jdbcType=BIGINT}
and hash is not null
-- and status = '2'

select count(DISTINCT `user_id`)
from t_vote_user
where vote_id = #{voteId,jdbcType=BIGINT}
and opt_id = #{optId,jdbcType=BIGINT}
and status = '2'

mapper.update:
update t_vote_user
set hash = #{hash,jdbcType=VARCHAR},
status = '1'
where user_id = #{userId}
and vote_id = #{voteId}

update t_vote_user
set status = #{status}
where user_id = #{userId}
and vote_id = #{voteId}

mapper.delete:
delete from t_vote_user
where vote_id=#{voteId,jdbcType=BIGINT}
and user_id=#{userId,jdbcType=BIGINT}
